INSERT INTO training VALUES 
    (null,'Yoga','Dynamisk yogaform där kraftfulla rörelser förenas med andning och fokusering.', 'exp', null),
    (null,'Step','Step är konditionsträning med koreograferade stegsekvenser i ett flöde på en stepbräda. Passar dig som vill utmana din koordination med pulshöjande träning till drivande musik.', 'exp', null),
    (null, 'Tabata', 'TABATA övningar, intervallträning med egen kropp, vikter och bräda. Svettig genomkörare för hela kroppen. Vi jobbar 20 sek, vilar 10 sek och upprepar det 8 ggr/övn = 4 min.', 'exp', null),
    (null, '3D-träning', 'Stationsträning i kroppens alla rörelseplan. ', 'exp', null),
    (null, 'Kettlebells', 'Redskapet Kettlebells är en gjutjärns vikt med handtag som finns i olika vikter. Målsättningen är att du jobbar styrka, uthållighet, koordination, balans och puls då du använder stora mukelgrupper under hela passet.', 'exp', null),
    (null, 'KB and friends', 'Ett 45 min morgon pass med Kettlebells och olika redskap och även din eggen kropp. Du utgår hela tiden från samma plats i salen och ni gör alla samma övning under x antal sek.', 'exp', null),
    (null, 'TRX', 'TRX är ett spännband som är fäst i en ställning. Du jobbar stabiliserande muskler samtidigt som en deltagare gör liknande övningar med egen kropp på golvet. Ni byter sedan plats och övning med varandra.', 'exp', null),
    (null, 'VIPR and friends', 'Utmanande pass med 3 olika redskap VIPR rör, Kettlebells och Bulgarian bag. Du gör samma rörelse med alla redskapen som utmanar samma muskler fast på olika sätt då hävarmen hamnar olika.', 'exp', null),
    (null, 'Procedos Elements One (P.E one)', 'Ett förkoreograferat pass där du följer ledaren rörelser. Dut utgår från din egna matt (som du lånar av oss) och rör kroppen i  alla riktningar. Många utafall och stora röelser.', 'exp', null),
    (null, 'Bättre balans', 'Vi jobbar med grunderna i att finna balansen och jobbar upp balansen blandat med styrkeövningar som hjälper dig i vardagen. Inga liggande/sittande golvövningar.', 'exp', null),
    (null, 'HIIT', '"HögIntensiv IntervallTäning" Styrke,- och konditionsövningar tränas samtidigt i gruppen under 30 sek. 4-6 övningar varvas i 4 ronder.', 'exp', null),
